import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import PastelImg002 from '../../../PastelImg/pastch002.jpg';

function Regis() {
    const [formData, setFormData] = useState({
        nickname: '',
        correo: '',
        password: '',
        rol: 'USUARIO',
    });

    const [formErrors, setFormErrors] = useState({
        nickname: '',
        correo: '',
        password: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
        // Llamar a la función de validación cuando se cambia un campo
        validateField(name, value);
    };

    const validateField = async (fieldName, value) => {
        const errors = { ...formErrors };

        switch (fieldName) {
            case 'correo':
                const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
                errors.correo = !emailRegex.test(value) ? 'Ingresa un correo electrónico válido' : '';
                break;
            case 'password':
                errors.password = value.length < 6 ? 'La contraseña debe tener al menos 6 caracteres' : '';
                break;
            default:
                break;
        }

        // Validar el correo y nombre en el backend
        if (fieldName === 'correo' || fieldName === 'nickname') {
            try {
                const response = await fetch(`http://localhost:8000/api/listarRegistro-${fieldName}/${value}`);
                const data = await response.json();

                if (!data.valid) {
                    errors[fieldName] = data.message;
                }
            } catch (error) {
                console.error(`Error al validar ${fieldName}:`, error);
            }
        }

        setFormErrors(errors);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (Object.values(formErrors).every((error) => error === '')) {
            try {
                const response = await fetch('http://localhost:8000/api/guardarRegistro', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(formData),
                });

                if (response.ok) {
                    console.log('Registro exitoso');
                    alert('¡Registro exitoso!');
                } else {
                    console.error('Error en el registro');
                    alert('Error en el registro');
                }
            } catch (error) {
                console.error('Error en la solicitud:', error);
            }
        } else {
            alert('Por favor, corrige los errores en el formulario antes de enviar.');
        }
    };

    return (
        <div>
            <div className="container-ini">
                <div className="right-ini">
                    <img className='img001' src={PastelImg002} alt='' />
                </div>
                <div className="left-ini">
                    <div className="div-ini">
                        <section className="form003 fondo002">
                            <div className="form004">
                                <div className="form005">
                                    <h3>Crea tu cuenta.</h3>
                                    <form action="" className="form006">
                                        <div className={`input003 ${formErrors.nickname ? 'has-error' : ''}`}>
                                            <input
                                                type="text"
                                                name="nickname"
                                                id="nickname"
                                                placeholder="Nombre Completo"
                                                className="input004"
                                                value={formData.nickname}
                                                onChange={handleInputChange}
                                                required
                                            />
                                            {formErrors.nickname && (
                                                <div className="error-message">{formErrors.nickname}</div>
                                            )}
                                        </div>
                                        <div className={`input003 ${formErrors.correo ? 'has-error' : ''}`}>
                                            <input
                                                type="text"
                                                name="correo"
                                                id="correo"
                                                placeholder="Correo Electronico"
                                                className="input004"
                                                value={formData.correo}
                                                onChange={handleInputChange}
                                                required
                                            />
                                            {formErrors.correo && (
                                                <div className="error-message">{formErrors.correo}</div>
                                            )}
                                        </div>
                                        <div className={`input003 ${formErrors.password ? 'has-error' : ''}`}>
                                            <input
                                                type="password"
                                                name="password"
                                                id="password"
                                                placeholder="Contraseña"
                                                className="input004"
                                                value={formData.password}
                                                onChange={handleInputChange}
                                                required
                                            />
                                            {formErrors.password && (
                                                <div className="error-message">{formErrors.password}</div>
                                            )}
                                        </div>
                                        {/* Campo oculto para el rol */}
                                        <input type="hidden" name="rol" value={formData.rol} />
                                        <button href="/Iniciar-Sesion" type="submit" className="btn004" onClick={handleSubmit}>
                                            Crear cuenta
                                        </button>
                                    </form>
                                    <p>
                                        Inicia sesión,<h1> </h1>
                                        <Link to="/Iniciar-Sesion" className="link002 link003">ahora!</Link>
                                    </p>
                                </div>
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Regis;
