import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import PastelImg002 from '../../../PastelImg/pastch002.jpg';

function Regis() {
    const [formData, setFormData] = useState({
        nickname: '',
        correo: '',
        password: '',
    });
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:8000/api/guardarRegistro', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                // El registro fue exitoso, puedes redirigir al usuario a una página de éxito o hacer algo más.
                console.log('Registro exitoso');
                alert('registro exitoso');
            } else {
                const errorData = await response.json(); // Obtener detalles del error desde el backend
                console.error('Error en el registro:', errorData.message);
                alert('Error en el registro: ' + errorData.message);
            }
        } catch (error) {
            console.error('Error en la solicitud:', error);
        }
    };

    return (

        <div>
            <div className="container-ini">
                <div className="right-ini">
                    <img className='img001' src={PastelImg002} alt='' />
                </div>
                <div className="left-ini">
                    <div className="div-ini">
                        <section className="form003 fondo002">
                            <div className="form004">
                                <div className="form005">
                                    <h3>Crea tu cuenta.</h3>
                                    <form action="" className="form006">
                                        <div className="input003">
                                            <input
                                                type="text"
                                                name="nickname"
                                                id="nickname"
                                                placeholder="Nombre Completo"
                                                className="input004"
                                                value={formData.nickname}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="input003">
                                            <input
                                                type="text"
                                                name="correo"
                                                id="correo"
                                                placeholder="Correo Electronico"
                                                className="input004"
                                                value={formData.correo}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <div className="input003">
                                            <input
                                                type="password"
                                                name="password"
                                                id="password"
                                                placeholder="Contrasena"
                                                className="input004"
                                                value={formData.password}
                                                onChange={handleInputChange}
                                                required
                                            />
                                        </div>
                                        <button type="submit" className="btn004" onClick={handleSubmit}>
                                            Crear cuenta
                                        </button>
                                    </form>
                                    <p>
                                        Inicia sesión,<h1> </h1>
                                        <Link to="/Iniciar-Sesion" className="link002 link003">ahora!</Link>
                                    </p>
                                </div>
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Regis
